INSERT INTO lessons (title, description, subject, grade_level, estimated_minutes, content_markdown, points_value, is_active, quiz_questions) VALUES
-- G2 Reading (8)
('Prefixes: UN and RE', 'Word beginnings that change meaning!', 'reading', 2, 12, '# Prefixes Change Words!\n\n**UN-** means NOT:\n- happy â†’ **un**happy (not happy)\n- kind â†’ **un**kind (not kind)\n\n**RE-** means AGAIN:\n- read â†’ **re**read (read again)\n- do â†’ **re**do (do again)', 15, true, '[{"question":"Unhappy means...","options":["Very happy","Not happy","Always happy"],"correct":1}]'),
('Suffixes: -LY and -FUL', 'Word endings that add meaning!', 'reading', 2, 12, '# Suffixes Add Meaning!\n\n**-LY** tells HOW:\n- quick â†’ quick**ly** (how you move)\n- slow â†’ slow**ly**\n\n**-FUL** means FULL OF:\n- hope â†’ hope**ful** (full of hope)\n- care â†’ care**ful**', 15, true, '[{"question":"Carefully means...","options":["With care","Without care","Caring less"],"correct":0}]'),
('Main Idea', 'What is the story mainly about?', 'reading', 2, 15, '# Finding the Main Idea ğŸ’¡\n\nThe main idea is what the story is MOSTLY about.\n\n**How to find it:**\n1. Read the whole text\n2. Ask: What is this mostly about?\n3. The title often gives clues\n\n**Example:** A story about a dog learning tricks â†’ Main idea: A dog learns new tricks.', 15, true, '[{"question":"Main idea is...","options":["A small detail","What it is mostly about","The first sentence"],"correct":1}]'),
('Character Traits', 'Words that describe characters', 'reading', 2, 12, '# Describing Characters\n\nCharacter traits tell us WHO someone is:\n\nğŸ˜Š **Kind** - Helps others\nğŸ’ª **Brave** - Faces fears\nğŸ§  **Clever** - Solves problems\nğŸ˜¤ **Stubborn** - Won''t change mind\nğŸ˜ **Funny** - Makes people laugh', 15, true, '[{"question":"A brave character...","options":["Runs from danger","Faces their fears","Is always sad"],"correct":1}]'),
('Fiction vs Nonfiction', 'Made-up or real?', 'reading', 2, 12, '# Two Types of Books\n\nğŸ“– **FICTION** - Made-up stories\n- Fairy tales\n- Fantasy\n- Stories with talking animals\n\nğŸ“š **NONFICTION** - Real information\n- Science books\n- Biographies\n- How-to books', 15, true, '[{"question":"A book about a talking dragon is...","options":["Fiction","Nonfiction","Neither"],"correct":0}]'),
('Context Clues', 'Figure out word meanings!', 'reading', 2, 15, '# Using Context Clues ğŸ”\n\nWhen you don''t know a word, look for CLUES nearby!\n\n**Example:** The enormous elephant was huge!\n"Enormous" means = HUGE (the clue!)\n\n**Tips:**\n- Read the sentence around it\n- Look for synonyms\n- Use picture clues', 15, true, '[{"question":"Context clues help us...","options":["Skip words","Understand word meanings","Forget words"],"correct":1}]'),
('Asking Questions', 'Good readers ask questions!', 'reading', 2, 12, '# Question Words â“\n\n**WHO** - Which person?\n**WHAT** - Which thing?\n**WHEN** - What time?\n**WHERE** - Which place?\n**WHY** - What reason?\n**HOW** - In what way?\n\nAsking questions helps you understand!', 15, true, '[{"question":"WHERE asks about...","options":["Time","Place","Reason"],"correct":1}]'),
('Poetry Reading', 'Poems are fun to read!', 'reading', 2, 15, '# Reading Poetry ğŸ­\n\n**Poems have:**\n- Rhymes (words that sound alike)\n- Rhythm (a beat)\n- Feelings and images\n\n**Example:**\n*The cat sat on the mat,*\n*Wearing a funny hat!*\n\n"Cat," "mat," and "hat" rhyme!', 15, true, '[{"question":"Poems often have...","options":["Rhymes","Only facts","No feelings"],"correct":0}]'),
-- G2 Math (8)
('Even and Odd', 'Numbers have partners - or not!', 'math', 2, 12, '# Even & Odd Numbers\n\n**EVEN** numbers pair up:\n2, 4, 6, 8, 10... (ends in 0,2,4,6,8)\n\n**ODD** numbers have one left over:\n1, 3, 5, 7, 9... (ends in 1,3,5,7,9)\n\nğŸ’¡ Even numbers can be split in half evenly!', 15, true, '[{"question":"Is 7 even or odd?","options":["Even","Odd","Neither"],"correct":1}]'),
('Skip Counting by 5s', 'Count faster with 5s!', 'math', 2, 10, '# Counting by 5s\n\n5, 10, 15, 20, 25, 30, 35, 40, 45, 50!\n\n**Tip:** Count nickels this way!\nğŸª™ğŸª™ğŸª™ğŸª™ = 5, 10, 15, 20 cents\n\n**Pattern:** Always ends in 0 or 5!', 12, true, '[{"question":"What comes after 25 when counting by 5s?","options":["26","30","35"],"correct":1}]'),
('Addition with Regrouping', 'When ones add up to 10 or more!', 'math', 2, 15, '# Regrouping (Carrying)\n\n**Problem:** 28 + 15 = ?\n\n**Step 1:** Add ones: 8 + 5 = 13\nThat''s 1 ten and 3 ones!\n\n**Step 2:** Carry the 1 to tens\n2 + 1 + 1 = 4 tens\n\n**Answer:** 43', 15, true, '[{"question":"28 + 15 = ?","options":["33","43","53"],"correct":1}]'),
('Subtraction with Regrouping', 'Borrowing from the tens!', 'math', 2, 15, '# Regrouping in Subtraction\n\n**Problem:** 42 - 17 = ?\n\n**Step 1:** Can''t take 7 from 2!\nBorrow 1 ten â†’ now 12 ones\n\n**Step 2:** 12 - 7 = 5\n**Step 3:** 3 - 1 = 2 (one ten was borrowed)\n\n**Answer:** 25', 15, true, '[{"question":"42 - 17 = ?","options":["25","35","15"],"correct":0}]'),
('Arrays', 'Rows and columns of objects!', 'math', 2, 12, '# Understanding Arrays\n\nğŸğŸğŸ\nğŸğŸğŸ\n\nThis array has:\n- 2 **rows** (across)\n- 3 **columns** (up and down)\n- 2 Ã— 3 = 6 apples total!\n\nArrays show multiplication!', 12, true, '[{"question":"An array with 3 rows of 4 has how many total?","options":["7","12","34"],"correct":1}]'),
('Measuring Length', 'How long is it?', 'math', 2, 12, '# Measuring with a Ruler ğŸ“\n\n**Inches** - Shorter things (pencils)\n**Feet** - Medium things (desks)\n**Yards** - Longer things (rooms)\n\n12 inches = 1 foot\n3 feet = 1 yard\n\nLine up at ZERO, read the number at the end!', 15, true, '[{"question":"How many inches in 1 foot?","options":["10","12","15"],"correct":1}]'),
('Word Problems', 'Math stories to solve!', 'math', 2, 15, '# Solving Word Problems\n\n**CUBES Strategy:**\n- **C**ircle the numbers\n- **U**nderline the question\n- **B**ox key words (total, left, more)\n- **E**valuate the operation (+, -, Ã—)\n- **S**olve and check!', 15, true, '[{"question":"\"Total\" usually means...","options":["Subtract","Add","Divide"],"correct":1}]'),
('Graphing Data', 'Show information with pictures!', 'math', 2, 12, '# Making Graphs ğŸ“Š\n\n**Bar Graph** - Bars show amounts\n**Pictograph** - Pictures show amounts\n\n**Reading a Graph:**\n1. Read the title\n2. Look at labels\n3. Count or compare bars/pictures', 15, true, '[{"question":"A pictograph uses...","options":["Bars","Pictures","Only numbers"],"correct":1}]'),
-- G2 Science (4)
('Habitats', 'Where do animals live?', 'science', 2, 15, '# Animal Habitats ğŸ \n\n**Desert** ğŸœï¸ - Hot, dry (camels, lizards)\n**Ocean** ğŸŒŠ - Saltwater (fish, whales)\n**Forest** ğŸŒ² - Trees (deer, squirrels)\n**Arctic** ğŸ§Š - Cold, icy (polar bears, penguins)\n\nAnimals fit their habitat!', 15, true, '[{"question":"Polar bears live in the...","options":["Desert","Ocean","Arctic"],"correct":2}]'),
('Food Chains', 'Who eats who?', 'science', 2, 15, '# The Food Chain ğŸ”—\n\nâ˜€ï¸ Sun gives energy to...\nğŸŒ± Plants (producers) eaten by...\nğŸ° Herbivores (plant-eaters) eaten by...\nğŸ¦ Carnivores (meat-eaters)\n\nEnergy flows through the chain!', 15, true, '[{"question":"Plants are called...","options":["Consumers","Producers","Decomposers"],"correct":1}]'),
('Properties of Matter', 'What is it like?', 'science', 2, 12, '# Describing Matter\n\n**Properties** tell us about objects:\n\n- **Size** - big or small?\n- **Color** - red, blue, green?\n- **Shape** - round, square?\n- **Texture** - smooth or rough?\n- **Weight** - heavy or light?', 15, true, '[{"question":"Texture describes...","options":["Color","How it feels","Size"],"correct":1}]'),
('Push and Pull', 'Forces make things move!', 'science', 2, 12, '# Push and Pull Forces ğŸ’ª\n\n**PUSH** = Move away from you\n- Pushing a door\n- Throwing a ball\n\n**PULL** = Move toward you\n- Opening a drawer\n- Pulling a wagon\n\nForces make things move!', 15, true, '[{"question":"Opening a drawer is a...","options":["Push","Pull","Neither"],"correct":1}]'),
