INSERT INTO lessons (title, description, subject, grade_level, estimated_minutes, content_markdown, points_value, is_active, quiz_questions) VALUES
-- G7 Math (12 more)
('Two-Step Equations', 'More complex equations', 'math', 7, 25, '# Two-Step Equations\n\n**Steps:**\n1. Undo addition/subtraction first\n2. Then undo multiplication/division\n\n2x + 5 = 13\n2x = 8\nx = 4', 25, true, '[{"question":"Solve: 3x + 2 = 14","options":["x=2","x=3","x=4","x=5"],"correct":2}]'),
('Proportional Relationships', 'Constant of proportionality', 'math', 7, 25, '# Proportional Relationships\n\ny = kx where k is constant\n\n**Example:** y = 3x\nWhen x=2, y=6\nWhen x=4, y=12\n\nGraph is a straight line through origin!', 25, true, '[{"question":"In y=5x, when x=3, y=?","options":["5","8","15","35"],"correct":2}]'),
('Slope and Rate of Change', 'Steepness of lines', 'math', 7, 28, '# Slope\n\n**Slope = rise/run = (y₂-y₁)/(x₂-x₁)**\n\nPositive slope: goes up\nNegative slope: goes down\nZero slope: horizontal\nUndefined: vertical', 28, true, '[{"question":"Positive slope goes...","options":["Down","Up","Horizontal","Nowhere"],"correct":1}]'),
('Probability Basics', 'Likelihood of events', 'math', 7, 25, '# Probability\n\nP = favorable outcomes / total outcomes\n\nP(heads) = 1/2 = 0.5 = 50%\n\n0 = impossible\n1 = certain', 25, true, '[{"question":"Probability of rolling 6 on a die?","options":["1/2","1/6","1/3","6"],"correct":1}]'),
('Surface Area', '3D shape coverings', 'math', 7, 28, '# Surface Area\n\n**Rectangular prism:**\nSA = 2lw + 2lh + 2wh\n\n**Cube:**\nSA = 6s²\n\nIt''s the total area of all faces!', 28, true, '[{"question":"Cube with s=3, SA=?","options":["27","36","54","18"],"correct":2}]'),
('Combining Like Terms', 'Simplifying expressions', 'math', 7, 22, '# Combining Like Terms\n\n**Like terms** have same variable and power\n\n3x + 5x = 8x\n2y² + 4y² = 6y²\n\n3x + 2y cannot be combined!', 22, true, '[{"question":"5x + 3x = ?","options":["8x","15x","8x²","2x"],"correct":0}]'),
('Scale Drawings', 'Maps and models', 'math', 7, 22, '# Scale Drawings\n\n**Scale** = drawing : actual\n\n1 inch : 10 feet\n\nIf drawing is 3 inches:\nActual = 3 × 10 = 30 feet', 22, true, '[{"question":"Scale 1:100, drawing is 5cm. Actual?","options":["5cm","50cm","500cm","5000cm"],"correct":2}]'),
('Circumference and Area of Circles', 'Pi calculations', 'math', 7, 25, '# Circles\n\n**Circumference:** C = 2πr or C = πd\n**Area:** A = πr²\n\nπ ≈ 3.14\n\nCircle with r=5:\nC ≈ 31.4, A ≈ 78.5', 25, true, '[{"question":"Area formula for circle?","options":["2πr","πd","πr²","2πr²"],"correct":2}]'),
('Simple and Compound Interest', 'Money growth', 'math', 7, 28, '# Interest\n\n**Simple:** I = Prt\n**Compound:** A = P(1 + r)^t\n\nP = principal\nr = rate\nt = time\n\nCompound grows faster!', 28, true, '[{"question":"Which grows faster?","options":["Simple","Compound","Same","Neither"],"correct":1}]'),
('Inequalities with Variables', 'Solving inequalities', 'math', 7, 25, '# Solving Inequalities\n\nSame as equations, BUT:\nWhen multiplying/dividing by negative, FLIP the sign!\n\n-2x > 6\nx < -3 (flipped!)', 25, true, '[{"question":"Divide by negative, you...","options":["Add","Subtract","Flip sign","Nothing"],"correct":2}]'),
('Rational Numbers', 'Fractions, decimals, integers', 'math', 7, 22, '# Rational Numbers\n\nCan be written as a fraction a/b\n\nIncludes: integers, fractions, terminating decimals, repeating decimals\n\n√2 is NOT rational (irrational)', 22, true, '[{"question":"Which is irrational?","options":["1/2","0.5","√2","3"],"correct":2}]'),
('Graphing Linear Equations', 'Lines on coordinate plane', 'math', 7, 28, '# Graphing Lines\n\n**y = mx + b**\nm = slope\nb = y-intercept\n\nPlot b first, then use slope to find more points!', 28, true, '[{"question":"In y=mx+b, b is...","options":["Slope","Y-intercept","X-intercept","Variable"],"correct":1}]'),
-- G7 Reading (8 more)
('Historical Fiction', 'Stories set in the past', 'reading', 7, 25, '# Historical Fiction\n\n**Characteristics:**\n- Set in real historical period\n- Blend of real and fictional characters\n- Historically accurate details\n- Fictional plot\n\nExamples: Number the Stars, Roll of Thunder', 25, true, '[{"question":"Historical fiction is...","options":["All true","All made up","Mix of real and fiction","Future setting"],"correct":2}]'),
('Narrative Techniques', 'How authors tell stories', 'reading', 7, 25, '# Narrative Techniques\n\n**Flashback** = jump to past\n**Foreshadowing** = hints future\n**Pacing** = speed of story\n**Suspense** = keeping reader curious\n**Dialogue** = reveals character', 25, true, '[{"question":"Jumping to the past is...","options":["Foreshadowing","Flashback","Pacing","Suspense"],"correct":1}]'),
('Satire and Irony', 'Humor with a message', 'reading', 7, 25, '# Satire & Irony\n\n**Satire** = using humor to criticize\n**Verbal irony** = saying opposite of meaning\n**Situational irony** = unexpected outcome\n**Dramatic irony** = audience knows more', 25, true, '[{"question":"Using humor to criticize?","options":["Irony","Satire","Parody","Comedy"],"correct":1}]'),
('Evaluating Arguments', 'Is it convincing?', 'reading', 7, 25, '# Evaluating Arguments\n\n**Check for:**\n- Clear claim\n- Relevant evidence\n- Logical reasoning\n- Addressing counterarguments\n- Reliable sources\n\n**Watch for:** emotional manipulation, false claims', 25, true, '[{"question":"Good arguments need...","options":["Emotions only","Relevant evidence","Loud voice","Many words"],"correct":1}]'),
('Greek and Latin Roots', 'Word origins', 'reading', 7, 22, '# Roots and Affixes\n\n**Common roots:**\n- bio = life\n- geo = earth\n- graph = write\n- scope = see\n- port = carry\n\nKnowing roots helps define new words!', 22, true, '[{"question":"Bio means...","options":["Earth","Write","Life","See"],"correct":2}]'),
('Synthesizing Multiple Sources', 'Combining information', 'reading', 7, 28, '# Synthesis\n\n**Steps:**\n1. Read multiple sources\n2. Identify common themes\n3. Note differences\n4. Create new understanding\n5. Cite all sources\n\nSynthesis ≠ summary!', 28, true, '[{"question":"Synthesis means...","options":["Copying","Summarizing","Combining sources","Ignoring"],"correct":2}]'),
('Analyzing Rhetoric', 'Persuasive strategies', 'reading', 7, 25, '# Rhetorical Analysis\n\n**Analyze:**\n- Speaker/author\n- Audience\n- Purpose\n- Context\n- Strategies (ethos, pathos, logos)\n\nWhy did they make these choices?', 25, true, '[{"question":"Rhetoric studies...","options":["Grammar","Persuasion","Spelling","Reading speed"],"correct":1}]'),
('Textual Evidence', 'Proving your points', 'reading', 7, 22, '# Using Evidence\n\n**Quote directly** when words matter\n**Paraphrase** for general ideas\n**Summarize** for main points\n\nAlways explain how evidence supports your claim!', 22, true, '[{"question":"When exact words matter, you should...","options":["Paraphrase","Summarize","Quote directly","Ignore"],"correct":2}]'),
-- G7 Science (10 more)
('Genetics Basics', 'Heredity and genes', 'science', 7, 30, '# Genetics\n\n**Gene** = section of DNA with instructions\n**Allele** = different versions of a gene\n**Dominant** = shows even with one copy (A)\n**Recessive** = needs two copies to show (a)\n\nGenotype → Phenotype', 30, true, '[{"question":"Dominant alleles are written as...","options":["Lowercase","Uppercase","Numbers","Symbols"],"correct":1}]'),
('Punnett Squares', 'Predicting traits', 'science', 7, 28, '# Punnett Squares\n\nPredict offspring genotypes!\n\nAa × Aa:\n   | A  | a\n---+----+----\nA  | AA | Aa\na  | Aa | aa\n\n25% AA, 50% Aa, 25% aa', 28, true, '[{"question":"Aa × Aa gives aa what percent?","options":["25%","50%","75%","100%"],"correct":0}]'),
('Atoms and Elements', 'Building blocks of matter', 'science', 7, 28, '# Atoms\n\n**Parts:**\n- Protons (+) in nucleus\n- Neutrons (neutral) in nucleus\n- Electrons (-) orbit nucleus\n\n**Atomic number** = number of protons\n**Element** = one type of atom', 28, true, '[{"question":"Protons have what charge?","options":["Negative","Positive","Neutral","None"],"correct":1}]'),
('Chemical Reactions', 'Changing substances', 'science', 7, 28, '# Chemical Reactions\n\n**Reactants** → **Products**\n\n**Signs of reaction:**\n- Color change\n- Gas bubbles\n- Temperature change\n- Precipitate forms\n\n**Conservation of mass:** atoms rearrange, not created/destroyed', 28, true, '[{"question":"In reactions, mass is...","options":["Created","Destroyed","Conserved","Changed"],"correct":2}]'),
('Waves and Energy', 'How energy travels', 'science', 7, 25, '# Waves\n\n**Transverse** = perpendicular motion (light)\n**Longitudinal** = parallel motion (sound)\n\n**Properties:**\n- Amplitude = height\n- Wavelength = distance between peaks\n- Frequency = waves per second', 25, true, '[{"question":"Light waves are...","options":["Longitudinal","Transverse","Neither","Both"],"correct":1}]'),
('Electromagnetic Spectrum', 'Types of radiation', 'science', 7, 28, '# EM Spectrum\n\nRadio → Microwave → Infrared → Visible → UV → X-ray → Gamma\n\n**Low frequency → High frequency**\n**Long wavelength → Short wavelength**\n**Low energy → High energy**', 28, true, '[{"question":"Which has highest energy?","options":["Radio","Visible","X-ray","Gamma"],"correct":3}]'),
('Human Body Systems Review', 'Systems working together', 'science', 7, 30, '# Body Systems\n\n**Circulatory** ↔ **Respiratory** = oxygen transport\n**Digestive** ↔ **Circulatory** = nutrient transport\n**Nervous** ↔ **All** = control and coordination\n\nHomeostasis = keeping balance!', 30, true, '[{"question":"Keeping balance is called...","options":["Metabolism","Homeostasis","Respiration","Circulation"],"correct":1}]'),
('Ecology and Biomes', 'Ecosystems of Earth', 'science', 7, 28, '# Biomes\n\n**Terrestrial:**\n- Tundra, Taiga, Forest, Desert, Grassland, Rainforest\n\n**Aquatic:**\n- Freshwater, Marine\n\nClimate determines biome!', 28, true, '[{"question":"Climate determines...","options":["Species only","Biome","Nothing","Weather only"],"correct":1}]'),
('Climate vs Weather', 'Short and long term', 'science', 7, 22, '# Climate vs Weather\n\n**Weather** = daily conditions (rain today)\n**Climate** = long-term patterns (tropical region)\n\n"Climate is what you expect, weather is what you get"', 22, true, '[{"question":"Long-term patterns are...","options":["Weather","Climate","Seasons","Storms"],"correct":1}]'),
('Natural Selection', 'Evolution basics', 'science', 7, 30, '# Natural Selection\n\n**Darwin''s theory:**\n1. Variation exists in populations\n2. More offspring than can survive\n3. Survival of the fittest\n4. Favorable traits inherited\n\n**Adaptation** = trait that helps survival', 30, true, '[{"question":"Who proposed natural selection?","options":["Newton","Einstein","Darwin","Mendel"],"correct":2}]'),
-- G7 Social (6 more)
('Age of Enlightenment', 'Ideas that changed the world', 'social', 7, 28, '# The Enlightenment\n\n**Key thinkers:**\n- Locke: natural rights\n- Montesquieu: separation of powers\n- Voltaire: free speech\n- Rousseau: social contract\n\nReason > tradition!', 28, true, '[{"question":"Who wrote about natural rights?","options":["Voltaire","Locke","Rousseau","Montesquieu"],"correct":1}]'),
('French Revolution', 'Liberty, Equality, Fraternity', 'social', 7, 28, '# French Revolution (1789-1799)\n\n**Causes:** Inequality, debt, Enlightenment ideas\n\n**Events:**\n- Storming of Bastille\n- Declaration of Rights of Man\n- Reign of Terror\n- Rise of Napoleon', 28, true, '[{"question":"French Revolution started in...","options":["1776","1789","1800","1812"],"correct":1}]'),
('Industrial Revolution', 'Machines change everything', 'social', 7, 28, '# Industrial Revolution\n\n**Started:** Britain, 1760s\n\n**Key inventions:**\n- Steam engine\n- Spinning jenny\n- Power loom\n- Railroads\n\n**Effects:** Urbanization, factories, new social classes', 28, true, '[{"question":"Industrial Revolution started in...","options":["France","Germany","Britain","USA"],"correct":2}]'),
('Civil War and Reconstruction', 'A nation divided', 'social', 7, 30, '# Civil War (1861-1865)\n\n**Causes:** Slavery, states'' rights, economics\n\n**Key events:**\n- Fort Sumter\n- Emancipation Proclamation (1863)\n- Gettysburg\n- Lee''s surrender at Appomattox\n\n**Reconstruction:** Rebuilding the nation', 30, true, '[{"question":"Emancipation Proclamation year?","options":["1861","1863","1865","1870"],"correct":1}]'),
('Immigration and Migration', 'Moving peoples', 'social', 7, 25, '# Immigration & Migration\n\n**Push factors:** war, poverty, persecution\n**Pull factors:** opportunity, freedom, family\n\n**US Immigration waves:**\n- Ellis Island (1892-1954)\n- Angel Island (1910-1940)', 25, true, '[{"question":"Push factors make people...","options":["Stay","Leave","Visit","Work"],"correct":1}]'),
('Civic Responsibility', 'Being a good citizen', 'social', 7, 22, '# Civic Duties & Responsibilities\n\n**Duties (required):**\n- Pay taxes\n- Jury duty\n- Obey laws\n\n**Responsibilities (voluntary):**\n- Voting\n- Volunteering\n- Staying informed', 22, true, '[{"question":"Voting is a...","options":["Duty","Responsibility","Law","Requirement"],"correct":1}]'),
